select.where:
 and sys_patrol_personnel.personnel_id = #{personnelId}
and sys_patrol.patrol_name like concat('%',
    #{patrolName}, '%')

and sys_patrol.patrol_describe =
    #{patrolDescribe}

and sys_patrol.patrol_principal =
    #{patrolPrincipal}

 and sys_patrol.region_id = #{regionId}
and psys_patrol.atrol_phone = #{patrolPhone}
and sys_patrol.patrol_status = #{patrolStatus}
and sys_patrol.patrol_start_time = #{patrolStartTime}
and sys_patrol.patrol_end_time = #{patrolEndTime}
and sys_patrol.patrol_create_time = #{patrolCreateTime}
and sys_patrol.patrol_actual_start_time = #{patrolActualStartTime}
and sys_patrol.patrol_actual_end_time = #{patrolActualEndTime}
and sys_patrol.type = 0

mapper.select:
SELECT sys_patrol.patrol_id, sys_patrol.patrol_name, sys_patrol.patrol_describe, sys_patrol.patrol_principal, sys_patrol.patrol_phone, sys_patrol.patrol_status, sys_patrol.patrol_start_time, sys_patrol.patrol_end_time, sys_patrol.patrol_create_time, sys_patrol.remark, sys_patrol.create_by, sys_patrol.create_time, sys_patrol.type, sys_patrol.region_id, sys_patrol.patrol_actual_start_time, sys_patrol.patrol_actual_end_time FROM sys_patrol LEFT JOIN sys_patrol_personnel ON sys_patrol.patrol_id = sys_patrol_personnel.patrol_id LEFT JOIN sys_personnel ON sys_patrol_personnel.personnel_id = sys_personnel.personnel_id
and sys_patrol_personnel.personnel_id = #{personnelId} and sys_patrol.patrol_name like concat('%', #{patrolName}, '%')
and sys_patrol.patrol_describe = #{patrolDescribe}
and sys_patrol.patrol_principal = #{patrolPrincipal}
and sys_patrol.patrol_phone = #{patrolPhone} and sys_patrol.patrol_status = #{patrolStatus} and sys_patrol.create_time >= #{patrolStartTime} and sys_patrol.create_time <= #{patrolEndTime} and sys_patrol.patrol_create_time = #{patrolCreateTime} and date_format(sys_patrol.update_time, '%y%m%d') = date_format(#{updateTime}, '%y%m%d') and sys_patrol.region_id = #{regionId}
AND sys_patrol.region_id IN ( SELECT region_id FROM sys_user_region WHERE user_id = #{userId} )
and sys_patrol.type = 0
